insert into ekzerceroj (id,ekzerco_id,kodo,numero,demando,respondo,normaligita,bildo,forigita) values (202,33,'ekz08_14',14,'Cet imb√©cile veut me rencontrer √† l‚Äô√©table. Qu‚Äôil [y] aille en premier, et qu‚Äôil m‚Äôattende. Je viendrai quand je voudrai.','Tiu stultulo volas renkonti min ƒâe la bovejo. Li unue iru tien kaj li atendu min. Mi venos kiam mi volos.','tiu stultulo volas renkonti min cxe la bovejo li unue iru tien kaj li atendu min mi venos kiam mi volos','',0);
