insert into ekzerceroj (id,ekzerco_id,kodo,numero,demando,respondo,normaligita,bildo,forigita) values (207,34,'ekz08_19',19,'Juƒùisto:  ƒàu vi do asertas, ke vi iris en la domon opiniante, ke ƒùi estas via domo¬†?<br/>Akuzato: Jes, sinjoro juƒùisto.<br/>Juƒùisto: Sed kial vi forkuris, kiam la sinjorino envenis¬†?<br/>Akuzato: Mi opiniis, ke ≈ùi estas mia edzino¬†!','Le juge: Donc, vous affirmez que vous √™tes entr√© dans la maison en pensant qu''elle √©tait votre maison.<br/>Accus√© : Oui, Monsieur le juge.<br/>Le juge: Mais pourquoi vous √™tes-vous enfui quand la femme est entr√©e ?<br/>Accus√©: J''ai pens√© qu''elle √©tait ma femme !','le juge donc vous affirmez que vous √™tes entr√© dans la maison en pensant qu elle √©tait votre maison<br/>accus√© oui monsieur le juge<br/>le juge mais pourquoi vous √™tes-vous enfui quand la femme est entr√©e<br/>accus√© j''ai pens√© qu elle √©tait ma femme','',0);
